pages/product:
---
product:
    videos:
        limit: {{theme_settings.productpage_videos_count}}
    reviews:
        limit: {{theme_settings.productpage_reviews_count}}
    related_products:
        limit: {{theme_settings.productpage_related_products_count}}
    similar_by_views:
        limit: {{theme_settings.productpage_similar_by_views_count}}
---
{{inject "productId" product.id}}
{{inject "zoomSize" theme_settings.zoom_size}}
{{inject "productSize" theme_settings.product_size}}

{{#partial "page"}}

    {{> components/common/breadcrumbs breadcrumbs=breadcrumbs}}

    {{#each product.shipping_messages}}
        {{> components/common/alert-info message}}
    {{/each}}



    <div>
        {{> components/elevate/product-view schema=true  }}

        {{{region name="product_below_content"}}}

        {{> components/elevate/product-tabs}}

        {{> components/products/schema}}
    </div>
{{/partial}}
{{> layout/base}}

components/elevate/product-view:
{{inject "bearerToken" settings.storefront_api.token}}
{{inject "showSwatch" theme_settings.show_card_swatch}}
{{#each product.reviews.messages}}
    {{#if error}}
        {{> components/common/alert-error error}}
    {{/if}}
    {{#if success}}
        {{> components/common/alert-success success}}
    {{/if}}
{{/each}}
<div class="productView" {{#if settings.data_tag_enabled}} data-event-type="product" data-entity-id="{{product.id}}" data-name="{{product.title}}" data-product-category="{{#each product.category}}{{#if @last}}{{this}}{{else}}{{this}}, {{/if}}{{/each}}" data-product-brand="{{product.brand.name}}" data-product-price="{{#if product.price.with_tax}}{{product.price.with_tax.value}}{{else}}{{product.price.without_tax.value}}{{/if}}" data-product-variant="single-product-option"{{/if}}>
    <section class="productView-details">
        <div class="productView-product"> 
            {{#if product.brand}}
                <h2 class="productView-brand">
                    <a href="{{product.brand.url}}"><span>{{product.brand.name}}</span></a>
                </h2>
            {{/if}}
            <h1 class="productView-title">{{product.title}}</h1>
            
            {{#if product.call_for_price}}
	            <div class="call-for-price-label">
	                {{product.call_for_price}}
	            </div>
            {{/if}}
            
            <div class="price-rating {{#if product.price}}with-price {{else}}without-price{{/if}}">
	            
                {{#or customer (if theme_settings.restrict_to_login '!==' true)}}
                    <div class="productView-price show">
                        {{> components/products/price price=product.price}}
                    </div>
                {{else}}
                    <div class="productView-price show">
                        {{> components/elevate/login-for-pricing}}
                    </div>
                {{/or}}
                
                

                <div class="productView-rating show">
                    {{#if settings.show_product_rating}}
                        {{> components/products/ratings rating=product.rating}}
                        <span class="productView-reviewLink product_review_count">
                            {{#if product.num_reviews '>' 0}}
                                <a href="{{product.url}}#product-reviews">
                                    {{lang 'products.reviews.link_to_review' total=product.num_reviews}}
                                </a>
                            {{else}}
                                {{lang 'products.reviews.link_to_review' total=product.num_reviews}}
                            {{/if}}
                        </span>
                    {{/if}}
                    {{#if settings.show_product_reviews}}
                        <span class="productView-reviewLink">
                            <a href="{{product.url}}{{#if is_ajax}}#write_review{{/if}}"
                               {{#unless is_ajax }}data-reveal-id="modal-review-form"{{/unless}} data-options='{"animation_speed": 0}'>
                               {{lang 'products.reviews.new'}}
                            </a>
                        </span>
                        {{> components/products/modals/writeReview}}
                    {{/if}}
                </div>
            </div>
        </div>
    </section>

    <section class="productView-images" data-image-gallery style="position: relative;">

    {{!--
        Note that these image sizes are coupled to image sizes used in /assets/js/theme/common/product-details.js
        for variant/rule image replacement
    --}}
   
        <div class="productView-image-main show" {{#if product.images}} {{else}}style="width: 100%"{{/if}}>

			<!--
			<div class="sale-flag-sash" >
	            <span class="sale-text">{{{theme_settings.pdp_sale_badge_label}}}</span>
	        </div>
	        -->

			
            <span class="icon-zoom-in theme-icon" data-icon="&#xe90f;"></span>
            {{!-- Remove the surrounding a-element if there is no main image. --}}
             <figure class="productView-image"
                     data-image-gallery-main
                     {{#if product.main_image}}
                     data-zoom-image="{{getImageSrcset product.main_image (cdn theme_settings.default_image_product) 1x=theme_settings.zoom_size }}"
                     {{/if}}>
                     
                     <div class="productView-img-container">
                        {{#if product.main_image}}
                        <a class="fancythumb-img" href="{{getImageSrcset product.main_image (cdn theme_settings.default_image_product) 1x=theme_settings.zoom_size}}" target="_blank"}>   
                        {{/if}}

                        <img class="productView-image--default-custom lazyload" data-sizes="auto" src="{{cdn 'img/loading.svg'}}" data-src="{{getImage product.main_image 'product_size' (cdn theme_settings.default_image_product)}}"
                            alt="{{product.main_image.alt}}" title="{{product.main_image.alt}}" data-main-image>
                            {{!-- Remove the surrounding a-element if there is no main image. --}}
                            
                        {{#if product.main_image}}
                            </a>
                        {{/if}}
                      </div>
             </figure>
        </div>

        {{#each product.images}}
        <a
            rel="gallery"
            data-fancybox="gallery1"
	        data-options='{"loop" : true}'
            style="display:none;"
            data-caption="{{this.alt}}"
            class="hiddengallery fancybox-hidden-img {{#if @index '==' 0}}currentGalleryImage{{/if}}"
            href="{{stripQuerystring (getImage this (cdn ../theme_settings.default_image_product))}}" rel="nofollow">
        </a>
        {{/each}}

        {{#if product.images}}
            <ul class="show productView-thumbnails {{#gt product.images.length 3}}productView-thumbnails-withslider{{/gt}}"{{#gt product.images.length 1}} data-slick='{
                "arrows": true,
                "dots": false,
                "autoplay": false,
                "autoplaySpeed": 5000,
                "infinite": true,
                "mobileFirst": true,
                "slidesToShow": 3,
                "slidesToScroll": 1,
                "responsive": [
                    {
                        "breakpoint": 500,
                        "settings": {
                            "slidesToShow": 3
                        }
                    },
                    {
                        "breakpoint": 767,
                        "settings": {
                            "slidesToShow": 4
                        }
                    },
                    {
                        "breakpoint": 790,
                        "settings": {
                            "slidesToShow": 4
                        }
                    },
                    {
                        "breakpoint": 1023,
                        "settings": {
                            "vertical": true,
                            "verticalSwiping": true,
                            "slidesToShow": 4
                        }
                    }
                ]
            }'{{/gt}}>
                {{#each product.images}}
                    <li class="" data-thumb-zoom-image="{{getImage this 'zoom_size' (cdn ../theme_settings.default_image_product)}}?imbypass=on">
                        <a class="productView-thumbnail-link {{#if (stripQuerystring (getImage ../product.main_image 'product_size' (cdn ../theme_settings.default_image_product))) '==' (stripQuerystring (getImage this 'product_size' (cdn ../theme_settings.default_image_product))) }}is-active is-main{{/if}}"
                            href="{{getImageSrcset this (cdn ../theme_settings.default_image_product) 1x=../theme_settings.zoom_size}}"
                            data-image-gallery-item
                            data-image-gallery-new-image-url="{{getImageSrcset this (cdn ../theme_settings.default_image_product) 1x=../theme_settings.product_size}}"
                            data-image-gallery-new-image-srcset="{{getImageSrcset this use_default_sizes=true}}"
                            data-image-gallery-zoom-image-url="{{getImageSrcset this (cdn ../theme_settings.default_image_product) 1x=../theme_settings.zoom_size}}"
                            data-link="{{stripQuerystring (getImage this (cdn ../theme_settings.default_image_product))}}"
                        >
                        {{> components/common/responsive-img
                            image=this
                            fallback_size=../theme_settings.productview_thumb_size
                            lazyload=../theme_settings.lazyload_mode
                        }}
                        </a>
                    </li>
                {{/each}}
            </ul>
        {{/if}}

    </section>

    <section class="productView-details">
	    
	    {{{region name="product_below_price"}}} 
	    
        <div class="productView-options">
            {{#if product.release_date }}
                <p>{{product.release_date}}</p>
            {{/if}}
            <form class="form" method="post" action="{{product.cart_url}}" enctype="multipart/form-data"
                  data-cart-item-add>
                <input type="hidden" name="action" value="add">
                <input type="hidden" name="product_id" value="{{product.id}}"/>
                <div data-product-option-change style="display:none;">
                    {{#each product.options}}
                        {{{dynamicComponent 'components/products/options'}}}
                    {{/each}}
                </div>
                <div class="form-field form-field--stock{{#unless product.stock_level}} u-hiddenVisually{{/unless}}">
                    <label class="form-label form-label--alternate">
                        {{lang 'products.current_stock'}}
                        <span data-product-stock>{{product.stock_level}}</span>
                    </label>
                </div>
                <div class="product-purchase-section">
	                {{#if product.can_purchase}}
                        {{> components/products/add-to-cart}}  
                    {{/if}} 
                </div>            
                {{#if product.out_of_stock}}
                    {{#if product.out_of_stock_message}}
                        {{> components/common/alert-error product.out_of_stock_message}}
                    {{else}}
                        {{> components/common/alert-error (lang 'products.sold_out')}}
                    {{/if}}
                {{/if}}
            </form>
            {{#if settings.show_wishlist}}
                {{> components/elevate/wishlist-dropdown}}
            {{/if}}
        </div>

        {{product.detail_messages}}
            <div class="productView-info">
                <dl class="productView-info-dl"{{#unless product.sku}} style="display: none;"{{/unless}}>
                    <dt class="productView-info-name sku-label"{{#unless product.sku}} style="display: none;"{{/unless}}>{{lang 'products.sku'}}</dt>
                    <dd class="productView-info-value" data-product-sku>{{product.sku}}</dd>
                </dl>
                
                <dl class="productView-info-dl"{{#unless product.upc}} style="display: none;"{{/unless}}>
                    <dt class="productView-info-name upc-label"{{#unless product.upc}} style="display: none;"{{/unless}}>{{lang 'products.upc'}}</dt>
                    <dd class="productView-info-value" data-product-upc>{{product.upc}}</dd>
                </dl>
                
                {{#if product.condition}}
                    <dl class="productView-info-dl">
                        <dt class="productView-info-name">{{lang 'products.condition'}}</dt>
                        <dd class="productView-info-value">{{product.condition}}</dd>
                    </dl>
                {{/if}}
                
                {{#if product.availability}}
                    <dl class="productView-info-dl">
                        <dt class="productView-info-name">{{lang 'products.availability'}}</dt>
                        <dd class="productView-info-value">{{product.availability}}</dd>
                    </dl>
                {{/if}}
                
                {{#all product.weight theme_settings.show_product_weight}}
                    <dl class="productView-info-dl">
                        <dt class="productView-info-name">{{lang 'products.weight'}}</dt>
                        <dd class="productView-info-value" data-product-weight>{{product.weight}}</dd>
                    </dl>
                {{/all}}
                
                {{#all product.width product.height product.depth theme_settings.show_product_dimensions}}
                    <dl class="productView-info-dl">
                        <dt class="productView-info-name">{{lang 'products.width'}}</dt>
                        <dd class="productView-info-value" data-product-width>
                            {{product.width}}
                            {{#if settings.measurements.length '==' 'Centimeters'}}
                            ({{lang 'products.measurement.metric'}})
                            {{else}}
                            ({{lang 'products.measurement.imperial'}})
                            {{/if}}
                        </dd>    
                    </dl>
                    
                    <dl class="productView-info-dl">
                        <dt class="productView-info-name">{{lang 'products.height'}}</dt>
                        <dd class="productView-info-value" data-product-height>
                            {{product.height}}
                            {{#if settings.measurements.length '==' 'Centimeters'}}
                            ({{lang 'products.measurement.metric'}})
                            {{else}}
                            ({{lang 'products.measurement.imperial'}})
                            {{/if}}
                        </dd>
                    </dl>
                    
                    <dl class="productView-info-dl">
                        <dt class="productView-info-name">{{lang 'products.depth'}}</dt>
                        <dd class="productView-info-value" data-product-depth>
                            {{product.depth}}
                            {{#if settings.measurements.length '==' 'Centimeters'}}
                            ({{lang 'products.measurement.metric'}})
                            {{else}}
                            ({{lang 'products.measurement.imperial'}})
                            {{/if}}
                        </dd>
                    </dl>
                {{/all}}
                {{#if product.min_purchase_quantity}}
                    <dl class="productView-info-dl">
                        <dt class="productView-info-name">{{lang 'products.min_purchase_quantity'}}</dt>
                        <dd class="productView-info-value">{{lang 'products.purchase_units' quantity=product.min_purchase_quantity}}</dd>
                    </dl>
                {{/if}}
                {{#if product.max_purchase_quantity}}
                    <dl class="productView-info-dl">
                        <dt class="productView-info-name">{{lang 'products.max_purchase_quantity'}}</dt>
                        <dd class="productView-info-value">{{lang 'products.purchase_units' quantity=product.max_purchase_quantity}}</dd>
                    </dl>
                {{/if}}
                {{#if product.gift_wrapping_available}}
                    <dl class="productView-info-dl">
                        <dt class="productView-info-name">{{lang 'products.gift_wrapping'}}</dt>
                        <dd class="productView-info-value">{{lang 'products.gift_wrapping_available'}}</dd>
                    </dl>
                {{/if}}
                {{#if product.shipping}}
                    {{#if product.shipping.calculated}}
                        <dl class="productView-info-dl">
                            <dt class="productView-info-name">{{lang 'products.shipping'}}</dt>
                            <dd class="productView-info-value">{{lang 'products.shipping_calculated'}}</dd>
                        </dl>
                    {{else}}
                        {{#if product.shipping.price.value '===' 0}}
                            <dl class="productView-info-dl">
                                <dt class="productView-info-name">{{lang 'products.shipping'}}</dt>
                                <dd class="productView-info-value">{{lang 'products.shipping_free'}}</dd>
                            </dl>
                        {{else}}
                            <dl class="productView-info-dl">
                                <dt class="productView-info-name">{{lang 'products.shipping'}}</dt>
                                <dd class="productView-info-value">{{lang 'products.shipping_fixed' amount=product.shipping.price.formatted}}</dd>
                            </dl>
                        {{/if}}
                    {{/if}}
                {{/if}}

                {{#if settings.bulk_discount_enabled}}
                    <div class="productView-info-bulkPricing">
                        {{> components/products/bulk-discount-rates bulk_discount_rates=product.bulk_discount_rates}}
                    </div>
                {{/if}}

                {{#if theme_settings.show_custom_fields_tabs '!==' true}}
                    <dl class="productView-info-dl">
                        {{#each product.custom_fields}}
                            <dt class="productView-info-name">{{name}}:</dt>
                            <dd class="productView-info-value">{{{value}}}</dd>
                        {{/each}}
                    </dl>
                {{/if}}
            </div>

        <div class="share-links">
            {{> components/common/share url=product.url}}
        </div>
    </section>
</div>


<!-- usp section start -->
<div class="product-usp">
    {{> components/elevate/usp-section}}
</div>
<!-- usp section end -->

{{{region name="product_above_content"}}}

<article class="productView-description">
    {{#if theme_settings.show_product_details_tabs}}
        {{> components/elevate/products-description-tabs}}
    {{else}}
        {{> components/elevate/products-description}}
    {{/if}}
</article>

<div id="previewModal" class="modal modal--large" data-reveal data-options='{"animation_speed": 0}'>
    <a href="#" class="modal-close" aria-label="{{lang 'common.close'}}" role="button">
        <span aria-hidden="true">&#215;</span>
    </a>
    <div class="modal-content"></div>
    <div class="loadingOverlay"></div>
</div>

components/elevate/footer:
{{#unless theme_settings.hide_footer_logo}} 
<div class="footer-logo">
    {{#if page_type '===' 'default'}}
        <div class="header-logo {{#if settings.store_logo.image}}logo-image{{else}}logo-text{{/if}}">
            {{> components/common/store-logo}}
        </div>
    {{else}} 
        <div class="header-logo {{#if settings.store_logo.image}}logo-image{{else}}logo-text{{/if}}">
            {{> components/common/store-logo}}
        </div>
    {{/if}}
</div>
{{/unless}} 

<footer class="footer" role="contentinfo">
    <div class="container">
        {{#unless theme_settings.hide_footer_menu}} 
        <div class="navPages-container footer-menu" id="menu" data-menu>
            <nav class="navPages">
                <ul class="navPages-list">
                    {{#if theme_settings.footer_first_nav_menu}}
                    <li class="navPages-item">
                        <a class="navPages-action" href="{{#if theme_settings.footer_first_nav_menu_link}}{{theme_settings.footer_first_nav_menu_link}}{{/if}}">
                            {{theme_settings.footer_first_nav_menu}}
                        </a>
                    </li>
                    {{/if}}
                    {{#if theme_settings.footer_second_nav_menu}}
                    <li class="navPages-item">
                        <a class="navPages-action" href="{{#if theme_settings.footer_second_nav_menu_link}}{{theme_settings.footer_second_nav_menu_link}}{{/if}}">
                            {{theme_settings.footer_second_nav_menu}}
                        </a>
                    </li>
                    {{/if}}
                    {{#if theme_settings.footer_third_nav_menu}}
                    <li class="navPages-item">
                        <a class="navPages-action" href="{{#if theme_settings.footer_third_nav_menu_link}}{{theme_settings.footer_third_nav_menu_link}}{{/if}}">
                            {{theme_settings.footer_third_nav_menu}}
                        </a>
                    </li>
                    {{/if}}
                    {{#if theme_settings.footer_fourth_nav_menu}}
                    <li class="navPages-item">
                        <a class="navPages-action" href="{{#if theme_settings.footer_fourth_nav_menu_link}}{{theme_settings.footer_fourth_nav_menu_link}}{{/if}}">
                            {{theme_settings.footer_fourth_nav_menu}}
                        </a>
                    </li>
                    {{/if}}
                    {{#if theme_settings.footer_fifth_nav_menu}}
                    <li class="navPages-item">
                        <a class="navPages-action" href="{{#if theme_settings.footer_fifth_nav_menu_link}}{{theme_settings.footer_fifth_nav_menu_link}}{{/if}}">
                            {{theme_settings.footer_fifth_nav_menu}}
                        </a>
                    </li>
                    {{/if}}
                    {{#if theme_settings.footer_sixth_nav_menu}}
                    <li class="navPages-item">
                        <a class="navPages-action" href="{{#if theme_settings.footer_sixth_nav_menu_link}}{{theme_settings.footer_sixth_nav_menu_link}}{{/if}}">
                            {{theme_settings.footer_sixth_nav_menu}}
                        </a>
                    </li>
                    {{/if}}
                    {{#if theme_settings.footer_seventh_nav_menu}}
                    <li class="navPages-item">
                        <a class="navPages-action" href="{{#if theme_settings.footer_seventh_nav_menu_link}}{{theme_settings.footer_seventh_nav_menu_link}}{{/if}}">
                            {{theme_settings.footer_seventh_nav_menu}}
                        </a>
                    </li>
                    {{/if}}
                    {{#if theme_settings.footer_eight_nav_menu}}
                    <li class="navPages-item">
                        <a class="navPages-action" href="{{#if theme_settings.footer_eight_nav_menu_link}}{{theme_settings.footer_eight_nav_menu_link}}{{/if}}">
                            {{theme_settings.footer_eight_nav_menu}}
                        </a>
                    </li>
                    {{/if}}
                </ul>
            </nav>
        </div> 
        {{/unless}}
        <div class="footer-content">
	        {{#unless theme_settings.hide_footer_menu_2}}
            <div class="footer-content-left show">
                <ul>
                    <li>
                        <a href="{{#if theme_settings.footer_first_webpage_link}}{{theme_settings.footer_first_webpage_link}}{{/if}}">{{#if theme_settings.footer_first_webpage}}{{theme_settings.footer_first_webpage}}{{/if}}</a>
                    </li>
                    <li>
                        <a href="{{#if theme_settings.footer_second_webpage_link}}{{theme_settings.footer_second_webpage_link}}{{/if}}">{{#if theme_settings.footer_second_webpage}}{{theme_settings.footer_second_webpage}}{{/if}}</a>
                    </li>
                    <li>
                        <a href="{{#if theme_settings.footer_third_webpage_link}}{{theme_settings.footer_third_webpage_link}}{{/if}}">{{#if theme_settings.footer_third_webpage}}{{theme_settings.footer_third_webpage}}{{/if}}</a>
                    </li>
                </ul>
            </div>
            {{/unless}}
            <div class="footer-content-middle show">
                <div class="footer-subcontent-middle">
                    {{#unless theme_settings.hide_footer_social_link}}
                    <div class="theme-social-icons">
                        {{> components/elevate/social-links}}
                    </div>
                    {{/unless}}
                    {{#unless theme_settings.hide_footer_newsletter}}
                    <div class="theme-newsletter">
                        {{#if settings.show_newsletter_box}}
                            {{> components/elevate/subscription-form}}
                        {{/if}}
                    </div>
                    {{/unless}}
                    
                    {{#unless theme_settings.hide_theme_payment}}
	                <div class="theme-payment show">
	                    {{> components/elevate/payment-icons}}
	                </div>
	                {{/unless}}
	                
	                {{{region name="ssl_site_seal--global"}}}
                </div>
            </div>
            <div class="footer-content-right show">
                {{#unless theme_settings.hide_theme_address}}
                <div class="theme-address show">
                    <address>{{nl2br settings.address}}</address>
                </div>
                {{/unless}}
                
                
                {{#if theme_settings.footer_phone_number}}
                <div class="theme-address footer-phone show">
                	<address>
                	
                	<svg enable-background="new 0 0 139 139" height="18px" id="Phone" version="1.1" viewBox="0 0 139 139" width="18px" xml:space="preserve" xmlns="http://www.w3.org/2000/svg" xmlns:xlink="http://www.w3.org/1999/xlink"><path d="M67.317,81.952c-9.284-7.634-15.483-17.054-18.742-22.414l-2.431-4.583c0.85-0.912,7.332-7.853,10.141-11.619  c3.53-4.729-1.588-9-1.588-9S40.296,19.933,37.014,17.076c-3.282-2.861-7.06-1.272-7.06-1.272  c-6.898,4.457-14.049,8.332-14.478,26.968C15.46,60.22,28.705,78.216,43.028,92.148c14.346,15.734,34.043,31.504,53.086,31.486  c18.634-0.425,22.508-7.575,26.965-14.473c0,0,1.59-3.775-1.268-7.06c-2.86-3.284-17.265-17.688-17.265-17.688  s-4.268-5.119-8.998-1.586c-3.525,2.635-9.855,8.496-11.38,9.917C84.171,92.749,73.582,87.104,67.317,81.952z"/></svg>
                	
                	<a href="tel:{{theme_settings.footer_phone_number}}">{{theme_settings.footer_phone_number}}</a>
                	
                	</address>
                </div>
                {{/if}}
                
                {{#if theme_settings.footer_email}}
                <div class="theme-address footer-email show">
	                
                	<address>
                	
                	<svg height="16px" enable-background:"new 0 0 16 16" version="1.1" viewBox="0 0 16 16" width="16px" xml:space="preserve" xmlns="http://www.w3.org/2000/svg" xmlns:xlink="http://www.w3.org/1999/xlink"><path d="M8,10c-0.266,0-0.5-0.094-1-0.336L0,6v7c0,0.55,0.45,1,1,1h14c0.55,0,1-0.45,1-1V6L9,9.664C8.5,9.906,8.266,10,8,10z M15,2  H1C0.45,2,0,2.45,0,3v0.758l8,4.205l8-4.205V3C16,2.45,15.55,2,15,2z"/></svg>

                	<a href="mailto:{{theme_settings.footer_email}}">{{theme_settings.footer_email}}</a>
                	
                	</address>
